package array;

public class Array02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] value = {50, 40, 30, 20, 10, 10, 20, 30, 40, 5};
		
		//다음 숫자들을 거꾸로 출력하시오.
		for (int i = value.length-1; i >= 0; i--) 
			
			//출력값
			System.out.println(value[i]);
		
	}

}
